sys_script.script:
(function executeRule(current, previous /*null when async*/) {
  gs.include('JiraRequest');
  var request = new JiraRequest();
  var response = request.deleteIssue(current.getValue('external_id'));

  if (response == 204 || response == "204") {
    return true;
  }

  return false;

})(current, previous);
