sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {
  var link = gs.getProperty('glide.servlet.uri') + 'sp?id=jira_issues_form&action=2&table=x_momo_jira_integr_issue&sys_id=' + current.getUniqueValue();
  template.print(link);
})(current, template, email, email_action, event);
