sp_widget.client_script:
function($scope, spUtil) {
  /* widget controller */
  var c = this;
  $scope.chart = $scope.data.chart;
  spUtil.recordWatch($scope, $scope.data.table, $scope.data.filter, function(response) {
    $scope.server.update().then(function(data) {
      $scope.chart.data = data.chart.data;
      $scope.chart.labels = data.chart.labels;
    });
  });

  $scope.getChartColors = function() {
    return $scope.data.chart.colors;
  };

  $scope.getClassName = function() {
    return 'chart-' + $scope.data.chart.type;
  };

  $scope.getChartOptions = function() {
    return $scope.data.chart.options;
  };
}

