sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
  var dset = getDataset({
    query: options.query,
    groupBy: options.groupBy,
    table: options.table,
    trendBy: options.trendBy,
    useSeries: options.type === 'bar',
    limitResults: options.limitResults
  });

  data.noResultsMsg = options.noResultsMsg;

  data.table = options.table;
  data.query = options.query;
  data.filter = options.filter || 'active=true^approval=approved';
  data.chart = {
    type: getChartTypeByOptions(options.chartType),
    colors: getChartColors(options.colors, dset.labels.length),
    title: options.title,
    data: dset.data,
    labels: dset.labels,
    legend: {
      display: true,
      position: 'bottom',
      align: 'center'
    }
  };

  data.canRead = gs.hasRole('x_momo_jira_integr.issue_tech');

  function getDataset(opts) {
    var dataset = {
      labels: [],
      data: []
    };
    var ga = new GlideAggregate(opts.table);
    ga.addAggregate('COUNT');
    ga.addEncodedQuery(opts.query);

    if (opts.groupBy) {
      ga.groupBy(opts.groupBy);
    }

    if (opts.trendBy) {
      ga.addTrend(opts.trendBy, 'Month');
    }

    ga.query();

    while (ga.next()) {
      if (opts.groupBy) {
        dataset.labels.push(ga.getDisplayValue(opts.groupBy));
      }
      dataset.data.push(ga.getAggregate('COUNT'));
    }
    dataset.data = dataset.data.map(function(d) {
      if (typeof d === 'string') {
        return opts.useSeries ? [ Number(d) ] : Number(d);
      }
      return opts.useSeries ? [ d ] : d;
    });
    return dataset;
  }


  function getChartColors(colors, maxLength) {
    if (!colors) return;
    if (!Array.isArray(colors)) return;

    return colors.slice(0, maxLength);
  }

  function getChartTypeByOptions(type) {
    var VALID_CHART_TYPES = [
      'doughnut',
      'pie',
      'line',
      'bubble',
      'radar',
      'polar-area',
      'bar',
      'horizontal-bar'
    ];

    if (!type) return 'bar';

    if (VALID_CHART_TYPES.indexOf(type) < 0) {
      return 'bar';
    }

    return type;
  }
})();
