sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
  gs.include('JiraRequestV2');

  var request = new JiraRequestV2();

  var issueTypes = request.send({
    path: 'issuetype'
  }).getResponse(function (results) {
    var host = gs.getProperty('x_momo_jira_integr.instance_name') + '/';
    if (!Array.isArray(results)) return [];
    return results.map(function (item) {
      return {
        external_id: item.id,
        description: item.description,
        icon: item.iconUrl !== host ? item.iconUrl : '',
        name: item.name
      };
    });
  });


  issueTypes.forEach(function (issueType) {


    var keys = Object.keys(issueType);

    var instance = new GlideRecord('x_momo_jira_integr_issue_type');
    instance.initialize();
    for (var i in keys) {
      instance[keys[i]] = issueType[keys[i]];
    }
    instance.project = current.sys_id.toString();
    instance.insert();
  });

})(current, previous);
