sys_script.script:
(function executeRule(current, previous) {
  gs.include('JiraRequestV2');

  var components = getAllComponentsFromProject(current.external_id);
  if (components.length > 0) {
    var alreadyExists = [];
    var componentIds = components.map(function(comp) {
      return comp.id;
    });
    var gr = new GlideRecord('x_momo_jira_integr_product');
    gr.addEncodedQuery('external_idIN' + componentIds.join(',') + '^active=true^project=' + current.sys_id.toString());
    gr.query();
    var newComponents = components;
    if (gr.next()) {
      while (gr.next()) {
        for (var i in componentIds) {
          if (gr.getValue('external_id') == componentIds[i]) {
            alreadyExists.push(gr.getValue('external_id'));
          }
        }
      }
      newComponents = components.filter(function(co) {
        return alreadyExists.indexOf(co.id) < 0;
      });
    }

    for (var c in newComponents) {
      var component = new GlideRecord('x_momo_jira_integr_product');
      component.initialize();
      component.external_id = newComponents[c].id;
      component.name = newComponents[c].name;
      component.description = newComponents[c].description;
      component.project = current.sys_id.toString();
      component.insert();
    }
  }


  function getAllComponentsFromProject(projectId) {
    var path = 'project/' + projectId + '/components';
    var request = new JiraRequestV2();
    return request.send({
      path: path
    }).getResponse(function(results) {
      if (!Array.isArray(results)) return [];
      return results;
    });
  }
})(current, previous);
