sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
  gs.include('JiraRequestV2');
  var issue = getJiraIssueFromChange(current.sys_id.toString());

  if (!issue) {
    return;
  }

  var issueType = issue.issue_type.sys_id.toString();

  updateCommentsFromClosureNotes(issue.getValue('external_id'), current.getValue('close_notes'));

  var status = new GlideRecord('x_momo_jira_integr_status');
  status.addQuery('issue_type', issueType);
  status.query();

  while (status.next()) {
    gs.error('Status: ' + status.getValue('name'));

    if (status.getValue('name') == 'Done') {
      issue.status = status.getUniqueValue();
      issue.setValue('active', false);
      issue.update();
      return;
    }
  }

  function updateCommentsFromClosureNotes(externalId, notes) {
    var request = new JiraRequestV2();
    var body = gs.getUserDisplayName() +
      ' added closure notes from ServiceNow:\n\n' + notes;
    request.setOptions({
      path: 'issue/' + externalId + '/comment',
      method: 'POST',
      body: {
        body: body,
        visibility: {
          type: 'role',
          value: 'Developers'
        }
      },
      async: true
    });
    request.send();
  }

  function getJiraIssueFromChange(changeRequestId) {
    var gr = new GlideRecord('x_momo_jira_integr_issue');
    gr.get('cab_request', changeRequestId);

    if (gr.isValidRecord()) {
      return gr;
    }
  }

})(current, previous);
