sys_script.script:
(function executeRule(current, previous /*null when async*/) {

  if (alreadyHasStandardTemplate()) {
    var prevParent = changeStandardTemplateField();
    updateStandardTemplatesMap(prevParent, current.getUniqueValue());
  } else {
    gs.addErrorMessage('No standard change template exists currently.');
  }

  function updateStandardTemplatesMap(prev, parent) {
    var gr = new GlideRecord('x_momo_jira_integr_standard_change_map');
    gr.addQuery('belongs_to', prev);
    gr.query();

    while (gr.next()) {
      gr.belongs_to = parent;
      gr.update();
    }
  }

  function changeStandardTemplateField() {
    var gr = new GlideRecord(current.getTableName());
    gr.addEncodedQuery('active=true^sys_id!=' + current.getUniqueValue() + '^change_template_field=true');
    gr.query();

    while (gr.next()) {
      gr.setValue('change_template_field', false);
      gr.update();
      return gr.getUniqueValue();
    }
  }

  function alreadyHasStandardTemplate() {
    var ga = new GlideAggregate(current.getTableName());
    ga.addAggregate('COUNT');
    ga.addEncodedQuery('active=true^sys_id!=' + current.getUniqueValue() + '^change_template_field=true');
    ga.query();
    while (ga.next()) {
      return Number(ga.getAggregate('COUNT')) > 0;
    }
    return false;
  }
})(current, previous);
