sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {
  var message = '';
  var updatedBy = current.getValue('sys_updated_by');
  if (event) {
    if (event.parm2) {
      message += event.parm2;
    } else if (/\./gi.test(updatedBy)) {
      message += updatedBy.split('.').join(' '); 
    } else {
      message = 'Someone';
    }
  }
  template.print(message + ' sent you a message!');
  email.setSubject(message + ' sent you a message!');
})(current, template, email, email_action, event);
