sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
  if (current.getValue('sys_updated_by') == 'guest') {
    return;
  }

  gs.include('JiraRequestV2');


  var external_id = current.getValue('external_id');

  var status = getExternalId('x_momo_jira_integr_status', current.getValue('status'));

  var request = new JiraRequestV2();

  var path = 'issue/' + external_id + '/transitions';

  var results = request.send({
    path: path
  }).getResponse();

  var transition = getActualTransitionId(results, status);

  if (transition) {
    request.send({
      method: 'POST',
      async: true,
      body: {
        transition: {
          id: transition
        }
      }
    });
  }

  function getActualTransitionId(response, externalId) {
    if (!response) return;
    var transitions = response.transitions.filter(function (transition) {
      return transition.to.id == externalId;
    }).map(function (elem) {
      return elem.id;
    });
    if (transitions.length > 0) {
      return transitions[0];
    }
  }

  function getExternalId(table, sys_id) {
    var instance = new GlideRecord(table);
    instance.addQuery('sys_id', sys_id.toString());
    instance.query();
    while (instance.next()) {
      return instance.getValue('external_id');
    }
  }

})(current, previous);
