sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
  if (current.getValue('sys_updated_by') == 'guest') return;

  gs.include('JiraRequestV2');

  var request = new JiraRequestV2();
  var body = {
    type: {
      name: current.type_id.name.toString()
    },
    outwardIssue: {
      id: current.outward_issues.external_id.toString()
    },
    inwardIssue: {
      id: current.inward_issue.external_id.toString()
    }
  };
  var created = request.send({
    path: 'issueLink',
    method: 'POST',
    body: body
  }).getResponse(function(results, status) {
    return (status == 200 || status == 204);
  });

  if (created) {
    var issuelinks = request.send({
      path: 'issue/' + current.inward_issue.external_id.toString(),
      qs: {
        fields: 'issuelinks'
      },
      method: 'GET',
      body: null
    }).getResponse(function(results) {
      if (!results) return;
      return results.fields.issuelinks;
    });

    if (Array.isArray(issuelinks)) {
      var type_id = current.type_id.external_id.toString();
      var table_name = current.getTableName();
      var linkId = getIssueLinkId(type_id, table_name, issuelinks);

      if (linkId && typeof linkId === 'string') {
        current.setValue("external_id", linkId);
        current.update();
      } else {
        current.deleteRecord();
      }
    }
  } else {
    current.deleteRecord();
  }
  function getIssueLinkId(typeId, table, issueLinks) {
    var issue = issueLinks.filter(function(link) {
      return link.type.id === typeId;
    }).map(function(item) {
      return item.id;
    })[0];

    var gr = new GlideRecord(table);
    gr.get("external_id", issue);
    if (gr.isValid()) return issue;
    return false;
  }

})(current, previous);
