sys_ui_action.script:
gs.include('JiraRequestV2');

var request = new JiraRequestV2();

request.send({ path: 'statuscategory' });

var results = request.getResponse();

if (Array.isArray(results)) {
  for (var r in results) {
    var category = results[r];
    var gr = new GlideRecord('x_momo_jira_integr_status_category');

    if (!gr.get("external_id", category.id)) {
      gr.initialize();
      gr.external_id = category.id;
      gr.external_key = category.key;
      gr.color = category.colorName;
      gr.name = category.name;
      gr.insert();
    }
  }
}

request.send({ path: 'status' }).getResponse(function(response) {
  if (Array.isArray(response)) {
    response.forEach(function(status) {
      var statusCategory = getStatusCategory(status.statusCategory.id);
      var gr = new GlideRecord('x_momo_jira_integr_status');
      gr.addQuery('external_id', status.id);
      gr.query();

      while (gr.next()) {
        gr.category = statusCategory;
        gr.update();
      }
    });
  }
});

function getStatusCategory(external_id) {
  var gr = new GlideRecord('x_momo_jira_integr_status_category');

  if (gr.get('external_id', external_id)) {
    return gr.getUniqueValue();
  }
}
