sys_ui_action.script:
gs.include('JiraRequestV2');
var change = changeTemplateFieldExists();

if (!change) {
  gs.addErrorMessage('You have not selected a field to be the standard change field. Please select the field, and all options will be updated');
} else {
  var request = new JiraRequestV2();
  var templates = getStandardChangeTemplates();
  var fieldId = change.getValue('field_id').replace('customfield_', '');
  var options = request.send({
    method: 'GET',
    path: 'customField/' + fieldId + '/option'
  }).getResponse();

  if (options) {
    var values = options.values;
    var newOptions = getNewStandardChangeOptions(values, templates);
    if (newOptions.length > 0) {
      request.send({
        method: 'POST',
        async: true,
        body: {
          options: newOptions
        }
      });
    } else {
      gs.addInfoMessage('All your standard templates are already up to date.');
    }
  }
}

function getNewStandardChangeOptions(options, templates) {
  var newTemplates = [];
  var util = new global.ArrayUtil();
  if (Array.isArray(templates) && Array.isArray(options)) {
    options = options.map(function (option) {
      return option.value;
    });
    for (var i = 0; i < templates.length; i++) {
      var name = templates[i];
      if (!util.contains(options, name)) {
        newTemplates.push(name);
      }
    }
  }
  if (newTemplates.length === 0) return [];

  return newTemplates.map(function (opt) {
    return {
      value: opt,
      cascadingOptions: []
    };
  });
}

function getStandardChangeTemplates() {
  var templates = [];
  var gr = new GlideRecord('std_change_record_producer');
  gr.addQuery('active', true);
  gr.query();

  while (gr.next()) {
    templates.push(gr.getValue('name'));
  }
  return templates;
}


function changeTemplateFieldExists() {
  var gr = new GlideRecord('x_momo_jira_integr_jira_fields');

  gr.get('change_template_field', true);

  if (gr.isValidRecord()) {
    return gr;
  }
  return false;
}
