sys_ui_script.script:
'use strict';

angular
  .module('jira.editor', ['ngAnimate'])
  .directive('markdownEditor', jiraMarkdown);



function jiraMarkdown() {

  return {
    restrict: "A",
    require:  'ngModel',
    scope: {
      onBlur: '&',
      onFocus: '&',
      showToolbarOnFocus: '@'
    },
    link: function(scope, element, attrs, ngModel) {
      $(element).markdown({
        saveable: false,
        autoFocus: true,
        resize: 'none',
        iconlibrary: 'fa',
        onBlur: onBlur,
        onSave: onSave,
        onChange: onChange,
        onFocus: onFocus,
        fullscreen: false,
        hiddenButtons: ['cmdPreview', 'cmdImage']
      });
      function onBlur(e) {
        $(element).prev().removeClass('active-toolbar');
        if (scope.onBlur) {
          return scope.onBlur(e)
        }

      }
      function onFocus(e) {
        $(element).prev().addClass('active-toolbar');
        if (scope.onFocus) {
          return scope.onFocus(e);
        }
      }
      function onChange(e) {
        ngModel.$setViewValue(e.getContent());
      }

      function onSave(e) {
        ngModel.$setViewValue(e.getContent());
      }
    }
  };
}
