sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
  gs.include('JiraAttachment');
  gs.include('JiraWebhookHelper');
  var jiraWebhookHelper = new JiraWebhookHelper(request, response);
  var attachmentHelper = new JiraAttachment();
  var body = request.body.data;
  var paramsId = request.pathParams.issue_id;
  var issue = jiraWebhookHelper.getJiraIssue(paramsId);

  if (body.webhookEvent == 'attachment_created') {
    attachmentHelper.createAttachmentFromContentUrl(issue, body.attachment);
    jiraWebhookHelper.setWebhookResponse(201);
  } else if (body.webhookEvent == 'attachment_deleted') {
    var status = 404;
    var attachment = getAttachmentRecord(body.attachment.id);
    if (attachment) {
      var deleted = attachmentHelper.deleteAttachment(attachment);
      if (deleted) {
        status = 201;
      }
    }
    jiraWebhookHelper.setWebhookResponse(status);
  }



  function getAttachmentRecord(external_id) {
    var gr = new GlideRecord('x_momo_jira_integr_issue_attachment');
    gr.get('external_id', external_id);
    if (gr.isValidRecord()) {
      return gr;
    }
  }

})(request, response);
