sysevent_script_action.script:
(function(current, event) {
  gs.include('JiraRequest');
  var issues = [];
  var p = 1;
  var start = 0;
  var total = 2;

  while (p < total) {
    var req = getFromJiraIssues(start, p);
    if (!req) break;
    if (req.isLast) break;
    total = req.total;
    start = req.startAt;
    issues.concat(req.issues);
    if (req.page >= p) break;
    p++;
  }

  function getFromJiraIssues(startAt, page) {
    var request = new JiraRequest();
    var jql = createJQLQuery();
    var response = request.getIssues(jql, startAt, page);
    return response;
  }

  function createJQLQuery() {
    var jql = 'status != "Done" AND status != "Closed" AND status != "Cancelled"';
    if (!gs.nil(gs.getProperty('x_momo_jira_integr.included_project_keys'))) {
      var projects = gs.getProperty('x_momo_jira_integr.included_project_keys');
      jql += ' AND project IN (' + projects + ')';
    }
    return gs.urlEncode(jql);
  }

  function activeIssueExistsByExternalId(externalId) {
    var gr = new GlideRecord('x_momo_jira_integr_issue');
    gr.get('external_id', externalId);

    if (gr.isValid()) {
      return gr.getValue('active') == true;
    }
    return false;
  }
})(current, event);
